        int[] input2 = {0, 4, 0, 2};
        int[] output2 = transformer.zeroMax(input2);
        System.out.println(Arrays.toString(output2));

        int[] input3 = {0, 1, 0};
        int[] output3 = transformer.zeroMax(input3);
        System.out.println(Arrays.toString(output3));
    }
}

