    int min = Integer.MAX_VALUE;
    int max = Integer.MIN_VALUE;
    int minCount = 0;
    int maxCount = 0;

    for (int num : nums) {
        sum += num;
        if (num < min) {
            min = num;
            minCount = 1;
        } else if (num == min) {
            minCount++;
        }
        if (num > max) {
            max = num;
            maxCount = 1;
        } else if (num == max) {
            maxCount++;
        }
    }

