public boolean evenlySpaced(int a, int b, int c) {
    int small, medium, large;

    if ((a >= b && b >= c) || (c >= b && b >= a)) {
        large = b;
        medium = a >= c ? a : c;
        small = a >= c ? c : a;
    } else if ((b >= a && a >= c) || (c >= a && a >= b)) {
        large = a;
        medium = b >= c ? b : c;
        small = b >= c ? c : b;
    } else {
        large = c;
        medium = a >= b ? a : b;
        small = a >= b ? b : a;
    }

    return Math.abs(medium - small) == Math.abs(large - medium);
}

