        return 0;
    }

    int clumpCount = 0;
    int lastNum = nums[0];
    int clumpSize = 1;

    for (int i = 1; i < nums.length; i++) {
        if (nums[i] == lastNum) {
            clumpSize++;
        } else {
            if (clumpSize >= 2) {
                clumpCount++;
            }
