public String zipZap(String str) {
    StringBuilder result = new StringBuilder();
    boolean removeNext = false;

    for (int i = 0; i < str.length(); i++) {
        char c = str.charAt(i);
        if (c == 'z') {
            removeNext = true;
        } else if (c == 'p') {
            removeNext = false;
        }
        if (!removeNext || (removeNext && i == str.length() - 1)) {
            result.append(c);
        }
    }

    return result.toString();
}

