public int centeredAverage(int[] nums) {
    int min = nums[0];
    int max = nums[0];
    int sum = 0;
    for (int num : nums) {
        if (num < min) {
            min = num;
        }
        if (num > max) {
            max = num;
        }
        sum += num;
    }
    for(int i = 0; i < nums.length; i++) {
        if(nums[i] == min) {
            nums[i] = nums[i+1];
        }
        if(nums[i] == max) {
            nums[i] = nums[i+1];
        }
    }
    return (sum - min - max) / (nums.length - 2);
}

