public String mixString(String a, String b) {
    StringBuilder sb = new StringBuilder();
    int aLen = a.length();
    int bLen = b.length();
    int len = Math.min(aLen, bLen);

    for (int i = 0; i < len; i++) {
        sb.append(a.charAt(i));
        sb.append(b.charAt(i));
    }

    if (aLen > len) {
        sb.append(a.substring(len));
    } else if (bLen > len) {
        sb.append(b.substring(len));
    }

    return sb.toString();
}

