    Stack<Character> stack = new Stack<Character>();
    for (char c : str.toCharArray()) {
        if (c == 'x') {
            stack.push(c);
        } else if (c == 'y') {
            while (!stack.isEmpty() && stack.peek() == 'x') {
                stack.pop();
            }
        }
    }
    return stack.isEmpty();
}

