    StringBuilder sb = new StringBuilder();
    int aLen = a.length();
    int bLen = b.length();
    int len = Math.min(aLen, bLen);

    for (int i = 0; i < len; i++) {
        sb.append(a.charAt(i));
        sb.append(b.charAt(i));
    }

    if (aLen > len) {
