public int centeredAverage(int[] nums) {
    if(nums.length < 3) {
        return 0;
    }

    int min = nums[0];
    int max = nums[0];
    int sum = 0;
    int count = 0;

    for(int i = 0; i < nums.length; i++) {
        if(nums[i] < min) {
            min = nums[i];
        }
        if(nums[i] > max) {
            max = nums[i];
        }
    }

    for(int i = 0; i < nums.length; i++) {
        if(nums[i] > min && nums[i] < max) {
            sum += nums[i];
            count++;
        }
    }

    return sum / count;
}

