}

public boolean twoTwo(int[] nums) {
    int count = 0;
    boolean hasTwo = false;

    for (int num : nums) {
        if (num == 2) {
            count++;
            hasTwo = true;
        }
    }

    if (!hasTwo) return true;

    if (count == 1) return false;

    int prev = -1;
    int curr = 0;
    int next = 0;

    for (int i = 0; i < nums.length; i++) {
        if (nums[i] == 2) {
            prev = curr;
            curr = next;
            next = 0;
        } else {
            next++;
        }

        if (curr > 0 && next > 0) return false;
    }

    return prev > 0 || curr > 0;
}

