    int[] maxOdds = new int[nums.length];
    int max = Integer.MIN_VALUE;
    for (int i = nums.length - 1; i >= 0; i--) {
        if (nums[i] % 2 != 0) {
            max = Math.max(max, nums[i]);
        }
        maxOdds[i] = max;
    }

