    for (int i = 0; i < len; i++) {
        sb.append(a.charAt(i));
        sb.append(b.charAt(i));
    }

    if (aLen > len) {
        sb.append(a.substring(len));
    } else if (bLen > len) {
        sb.append(b.substring(len));
    }

    return sb.toString();
}

