private int findLargestOddValue(int[] nums, int startIndex) {
    int largestOdd = 0;
    for (int i = startIndex + 1; i < nums.length; i++) {
        if (nums[i] > largestOdd && nums[i] % 2 != 0) {
            largestOdd = nums[i];
        }
    }
    return largestOdd;
}

