        i++;
    }

    int j = i + 1;
    while (j < nums.length && (nums[j] != 5 || (j > 0 && nums[j-1] == 4))) {
        j++;
    }

    while (i < nums.length) {
        if (nums[i] == 4 && (i == 0 || nums[i-1] != 5)) {
            if (nums[j] == 5) {
                int temp = nums[i+1];
                nums[i+1] = nums[j];
                nums[j] = temp;
            }
            while (j < nums.length && (nums[j] != 5 || (j > 0 && nums[j-1] == 4))) {
                j++;
            }
        }
        i++;
    }

    return nums;
}

